word =""



class Solution:
    def exist(self, board, word: str) -> bool:

        m=len(board)
        if m>0:
            n=len(board[0])

        if not word:
            return (True)

        scar=word[0]
        wl=len(word)
        for i in range (m):
            for j in range(n):
                if board[i][j]==scar:
                    if wl==1:
                        return True
                    #dictTrack={str(i)+str(j):True}
                    x=board[i][j]
                    board[i][j]='.'
                    if self.check(i,j,word[1:],board,m,n,wl-1):
                        return (True)
                    board[i][j]=x

        return (False)

    def check(self,i,j,word,board,m,n,wl):
        #dictTrack={str(i)+str(j):True}
        #for k in range(1,wl):
        tmp=word[0]
        find=False
    
        if not find and (j+1)<n and board[i][j+1]==tmp:
            if wl==1:
                return True
            find = True
            x=board[i][j+1]
            board[i][j+1]='.'
            res=self.check(i,j+1,word[1:],board,m,n,wl-1)
            if res:
                return res
            else:
                find =False
                board[i][j+1]=x
        if not find and (j-1)>=0 and board[i][j-1]==tmp:
            if wl==1:
                return True
            find = True
            x=board[i][j-1]
            board[i][j-1]='.'
            res=self.check(i,j-1,word[1:],board,m,n,wl-1)
            if res:
                return res
            else:
                find =False
                board[i][j-1]=x
            #j-=1
        if not find and (i+1)<m and board[i+1][j]==tmp:

            if wl==1:
                return True
            find = True
            x=board[i+1][j]
            board[i+1][j]='.'
            res=self.check(i+1,j,word[1:],board,m,n,wl-1)
            if res:
                return res
            else:
                find =False
                board[i+1][j]=x
        if not find and (i-1)>=0 and board[i-1][j]==tmp:

            if wl==1:
                return True
            find = True
            x=board[i-1][j]
            board[i-1][j]='.'
            res=self.check(i-1,j,word[1:],board,m,n,wl-1)
            if res:
                return res
            else:
                find =False
                board[i-1][j]=x
        if not find:
            return False
